import React, { useState } from 'react';
import './index.css';

function App(){
    let str = "";
    const [title, setTitle] = useState(str);
    let ftyped = "";
    let styped = "";
    function inputEvent1(event){
        ftyped = event.target.value;
    }
    function inputEvent2(event){
        styped = event.target.value;
        console.log(styped);
    }
    function change(){
        document.getElementsByClassName('text1').value
        setTitle(title => (ftyped+" " +styped));
    }
    return (<>
        <div>
            <h1>Hello {title}</h1>
            <input className='text1' type='text' placeholder='Enter your first name'
                onChange={inputEvent1}
            />
            <input className='text2' type='text' placeholder='Enter your last name'
                onChange={inputEvent2}
            />
            <button onClick={change}>Submit</button>
        </div>
    </>);
}
export default App;
