import React, { useState } from 'react';
import './index.css';

function App(){
    const time = new Date().toLocaleTimeString();
    const [currTime, setTime] = useState(time);
    function changeTime(){
        setTime(currTime => new Date().toLocaleTimeString()); // on the spot change nhi kar rha count ko, yeh function ke scope ke baahar change ho rha 
    }
    setInterval()
    return (<>
        <h1> {currTime}  </h1>
        {/* <button onClick={IncNum}>GET TIME</button> */}
    </>);
}
export default App;
