import React from 'react';
import ReactDOM from 'react-dom';

document.body.style = 'background : #BBEFF4';
let currTime = new Date().getHours();
const greetingkacolor = {}; //empty object
const divbox = {
    margin: 'auto',
    wight: '100%',
    height: '100vh',
    display: 'flex',
    backgroundColor: '#F4E6BB',
    textAlign: 'center',
    fontFamily: "'Josefin Sans', sans-serif",
    fontSize : '60px',


}
let morning = "hi";
if(currTime <= 12){
    morning = "Good Morning";
    greetingkacolor.color = 'green';
}
else if(currTime > 12 && currTime <= 16){
    morning = "Good Afternoon";
    greetingkacolor.color = 'orange';
}
else if(currTime > 16 && currTime <= 19){
    morning = "Good Evening";
    greetingkacolor.color = 'purple';
}
else{
    morning = "Good Night";
    greetingkacolor.color = 'Black';
}
ReactDOM.render(<div style={divbox}>
Hello sir, <span style={greetingkacolor}>
{
    morning
}
</span>
</div>, 
document.getElementById('root'),);
