import React from 'react';
import ReactDom from 'react-dom';
import './index.css';
const fname = 'Pralay';
const lname = 'Wandre';
const currdate = new Date().toLocaleDateString();
let currtime = new Date().toLocaleTimeString();

const img1 = "https://picsum.photos/200/300";
const img2 = "https://picsum.photos/250/300";
const img3 = "https://picsum.photos/260/300";
const img4 = "https://picsum.photos/270/300";
const link = "https://www.youtube.com/";
// ReactDom.render('kya dikhana hai', 'kaha dikhana hai', 'callback function') //render ka simple matlab hota hai matlab dikhana
// .name{
//     color: burlywood;
//     text-align: center;
//     text-transform: capitalize;
//     text-shadow: 0ch;
//     font-style: italic;
//   }
const name = {
    color : 'burlywood',
    textShadow : 'capitalize',
    fontStyle : 'italic', 
    textAlign: 'center',
}
ReactDom.render(<>
<h1 style={
    name
}>My name is {fname + " " + lname}</h1>
<p>{`Today's date is ${currdate}`}</p>
<p>{`The Current time is ${currtime}`}</p>
<div>
<img src={img1} alt = 'random1'/>
<img src={img2} alt = 'random2'/>
<img src={img3} alt = 'random3'/>
<a href= {link} target="_hihi">
    <img src={img4} alt = 'random4'/>
</a>
</div>
</>, 
document.getElementById('root')); 

