import React, { useState } from 'react';
import './index.css';

function App(){
    const currDate = new Date().getHours().toString();
    console.log(currDate);
    const [count, setCount] = useState(0);
    function IncNum(){
        setCount(count+1); // on the spot change nhi kar rha count ko, yeh function ke scope ke baahar change ho rha 
    }
    console.log(count);
    return (<>
        <h1> {count} </h1>
        <button onClick={IncNum}>Click me</button>
    </>);
}
export default App;
