import React from 'react';
import './index.css';

function App (){
let currtime = new Date(2023, 3, 11, 12).getHours();
let greeting = "";
let greetingkaColor = {};
if(currtime < 12){
    greeting = "Good Morning";
    greetingkaColor.color = 'Green';
}
else if(currtime >= 12 && currtime <= 17){
    greeting = "Good Afternoon";
    greetingkaColor.color = 'Green';
}
else if(currtime > 17 && currtime <= 19){
    greeting = "Good Evening";
    greetingkaColor.color = 'Purple';
}
else{
    greeting = "Good Night";
    greetingkaColor.color = 'Black';
}
return (<>
    <h1>Hello sir, <span style={greetingkaColor}>{greeting}</span></h1>
</>)
}
export default App;
